import java.util.*;
public class StringLength {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc= new Scanner(System.in);
		String s=sc.next();
		int S= s.length();
		System.out.println(s);
		System.out.println("String Length: " + S);

	}

}
